CREATE DATABASE NANDB
GO
USE NANDB 
GO

CREATE TABLE SONG
( 
	SONGID INT NOT NULL IDENTITY(1,1) PRIMARY KEY,
	SONGNAME NVARCHAR(MAX),
	SINGERNAME NVARCHAR(MAX),
	SONGURL VARCHAR(MAX),
	DOWNLOADURL VARCHAR(MAX),
	IMAGEURL VARCHAR(MAX),
	SAVEPATH NVARCHAR(MAX),
	DURATION FLOAT,
	POSITION FLOAT
)
ALTER TABLE SONG ADD LIKED BIT

CREATE TABLE UPLOADSONG
( 
	SONGID INT NOT NULL IDENTITY(1,1) PRIMARY KEY,
	SONGNAME NVARCHAR(MAX),
	SINGERNAME NVARCHAR(MAX),
	IMAGEPATH VARCHAR(MAX),
	SAVEPATH NVARCHAR(MAX),
	DURATION FLOAT,
	POSITION FLOAT
)

CREATE TABLE FEATUREDSONG
(
	SONGID INT NOT NULL PRIMARY KEY
	CONSTRAINT FK_FEATURED_SONG FOREIGN KEY (SONGID) REFERENCES SONG(SONGID)
)

CREATE TABLE NEWSONG
(
	SONGID INT NOT NULL PRIMARY KEY
	CONSTRAINT FK_NEW_SONG FOREIGN KEY (SONGID) REFERENCES SONG(SONGID)
)

CREATE TABLE USERS
(
	USERID INT NOT NULL IDENTITY(1,1) PRIMARY KEY,
	FULLNAME NVARCHAR(MAX),
	USERNAME VARCHAR(MAX),
	PASS VARCHAR(MAX),
	EMAIL VARCHAR(MAX),
	PHONE VARCHAR(50),
	DOB DATETIME,
	SEX VARCHAR(10)
)

CREATE TABLE LIKES
(
	SONGID INT NOT NULL,
	USERID INT NOT NULL,
	CONSTRAINT FK_LIKE_SONG FOREIGN KEY (SONGID) REFERENCES SONG(SONGID),
	CONSTRAINT FK_LIKE_USER FOREIGN KEY (USERID) REFERENCES USERS(USERID)
)

ALTER TABLE LIKES ADD CONSTRAINT PK_LIKES PRIMARY KEY (SONGID, USERID)

CREATE TABLE UPLOAD
(
	SONGID INT NOT NULL,
	USERID INT NOT NULL,
	CONSTRAINT FK_UPLOAD_SONG FOREIGN KEY (SONGID) REFERENCES SONG(SONGID),
	CONSTRAINT FK_UPLOAD_USER FOREIGN KEY (USERID) REFERENCES USERS(USERID)
)

ALTER TABLE UPLOAD DROP CONSTRAINT FK_UPLOAD_SONG
ALTER TABLE UPLOAD ADD CONSTRAINT FK_UPLOAD_SONG FOREIGN KEY (SONGID) REFERENCES UPLOADSONG(SONGID)

ALTER TABLE UPLOAD ADD CONSTRAINT PK_UPLOAD PRIMARY KEY (SONGID, USERID)

CREATE TABLE LASTEST
(
	SONGID INT NOT NULL,
	USERID INT NOT NULL,
	CONSTRAINT FK_LASTEST_SONG FOREIGN KEY (SONGID) REFERENCES SONG(SONGID),
	CONSTRAINT FK_LASTEST_USER FOREIGN KEY (USERID) REFERENCES USERS(USERID)
)

ALTER TABLE LASTEST ADD CONSTRAINT PK_LASTEST PRIMARY KEY (SONGID, USERID)
ALTER TABLE LASTEST ADD SEQ INT

CREATE TABLE PLAYLIST 
(
	PLAYLISTID INT NOT NULL IDENTITY(1,1) PRIMARY KEY,
	PLAYLISTNAME NVARCHAR(MAX),
	USERID INT NOT NULL,
	CONSTRAINT FK_PLAYLIST_USER FOREIGN KEY (USERID) REFERENCES USERS(USERID)
)

CREATE TABLE PLAYLIST_SONG
(
	SONGID INT NOT NULL,
	PLAYLISTID INT NOT NULL,
	CONSTRAINT FK_SONG FOREIGN KEY (SONGID) REFERENCES SONG(SONGID),
	CONSTRAINT FK_PLAYLIST FOREIGN KEY (PLAYLISTID) REFERENCES PLAYLIST(PLAYLISTID)
)
ALTER TABLE PLAYLIST_SONG ADD CONSTRAINT PK_PLAYLIST_SONG PRIMARY KEY (SONGID, PLAYLISTID)





